sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	// operande1 == Old account amount
	function addOperation(accountAmount, operationAmount){

		var total;
		var testIfOperationSucceed = true;

		try{ //TryAdd
			var operande1 = accountAmount;
			var	operande2 = operationAmount;
				//total = parseInt(operande1, 10) + parseInt(operande2, 10);	
				total = operande1 + operande2;
		}
		catch(addOperationError){
			testIfOperationSucceed = false;
			var jslogmessage;
			jslogmessage = 'A JavaScript runtime error occurred in addOperation :'; 
			jslogmessage = jslogmessage + addOperationError.message;
			jslog( jslogmessage );
		}
		if(testIfOperationSucceed){
			return total;
		}
		else{
			gs.error("FamousBank App: Operation Add unsuccessfull");
			total = accountAmount;
			return total;
		}
	}
	function withdrawOperation(accountAmount, operationAmount){
		var total;
		var testIfOperationSucceed = true;

		try{ //TryWithdraw
			var operande1 = accountAmount;
			var	operande2 = operationAmount;
				//total = parseInt(operande1, 10) - parseInt(operande2, 10);
			total = operande1 - operande2;
		}
		catch(withdrawOperationError){
			testIfOperationSucceed = false;
			var jslogmessage;
			jslogmessage = 'A JavaScript runtime error occurred in withdrawOperation :'; 
			jslogmessage = jslogmessage + withdrawOperationError.message;

			jslog( jslogmessage );
		}
		if(testIfOperationSucceed){
			return total;
		}
		else{
			gs.error("FamousBank App: Operation withdraw unsuccessfull");
			total = accountAmount;
			return total;
		}
	}

	try{ //TryQuery
		var account = new GlideRecord('x_281451_famousban_comptes'); // Nom de la table Comptes

		account.addQuery('u_reference_client', current.u_client_reference); //Va chercher le ou les comptes du clients
		// Get interdi ci-haut... Query sur Comptes interdi.

		account.query();

		try{ //TryOperation
			var accountAmount;
			var operationAmount;
			var newAccountAmount;
			//Parcourir tous les comptes du client et executer l'operation.
			// Si on ajoute une deuxième référence on pourra choisir un compte spécifique dans lequel faire l'operation
			while(account.next()){
				//Double check si on refere le bon client
				if(current.u_client_reference == account.getValue('u_reference_client')){


					accountAmount = account.u_amount; //account.getValue('u_amount'); //

					operationAmount = current.u_montant;

					var operationType = current.u_type;

					//Test le type d'operation pour appeller la fonction correspondante
					if(operationType == "add" || operationType == 1){ 
						// operande1 == Old account amount
						newAccountAmount = addOperation(accountAmount, operationAmount);
					}
					else if(operationType == "withdraw" || operationType == 2){ 
						newAccountAmount = withdrawOperation(accountAmount, operationAmount);
					}
					else{
						gs.error("FamousBank App: Operation type unknown");
						newAccountAmount = accountAmount;
						current.setAbortAction(true);
					}


					//Set Transaction TIME
					var rightnow = new GlideDateTime();
					current.u_transaction_date_time = rightnow;
					current.u_status = 'completed';

					//g_form.setValue('u_amount', newAccountAmount);
					account.setValue('u_amount', newAccountAmount); 

					account.update();
				}
			}
		}
		catch(TryOperationError){
			var jslogmessage2;
			jslogmessage2 = 'A JavaScript runtime error occurred in TryOperation :'; 
			jslogmessage2 = jslogmessage2 + TryOperationError.message;
			jslog( jslogmessage2 );
		}

	}
	catch(TryQueryError){
		var jslogmessage1;
			jslogmessage1 = 'A JavaScript runtime error occurred in TryQuery :'; 
			jslogmessage1 = jslogmessage1 + TryQueryError.message;
			jslog( jslogmessage1 );
	}

})(current, previous);
