function add(amountToAdd){ //current
	var testAddIfSucceed = true;
		try{ //Try1

			var accountValue = g_form.getValue('u_amount');

			try{ //Try2

				//var total = amountToAdd + accountValue;
				var total = parseInt(amountToAdd, 10) + parseInt(accountValue, 10);

				g_form.setValue('u_amount', total);
			}
			catch(errText2){
				testAddIfSucceed = false;
				var jslogmessage2;
				jslogmessage2 = 'A JavaScript runtime error occurred in add fct try2 :'; 
				jslog( jslogmessage2 + errText2.message);
			}

		}
		catch(errText1){
			testAddIfSucceed = false;
			var jslogmessage1;
				jslogmessage1 = 'A JavaScript runtime error occurred in add fct try1 :'; 
				jslog( jslogmessage1 + errText1.message);
		}




		return testAddIfSucceed;
	}
